import React from 'react';
import { useParams } from 'react-router-dom';
import { Building, GraduationCap, Users, Search } from 'lucide-react';

// Import user type specific components
import GovernmentDashboard from './GovernmentDashboard';
import InstituteDashboard from './InstituteDashboard';
import StudentDashboard from './StudentDashboard';
import RecruiterDashboard from './RecruiterDashboard';

interface UserTypeConfig {
  title: string;
  icon: React.ComponentType<{ className?: string }>;
  description: string;
  component: React.ComponentType;
}

const userTypeConfigs: Record<string, UserTypeConfig> = {
  government: {
    title: 'Admin Dashboard',
    icon: Building,
    description: 'Manage and verify official certificates',
    component: GovernmentDashboard,
  },
  institute: {
    title: 'Institute Dashboard',
    icon: GraduationCap,
    description: 'Issue and manage student certificates',
    component: InstituteDashboard,
  },
  student: {
    title: 'Student Portal',
    icon: Users,
    description: 'View and share your certificates',
    component: StudentDashboard,
  },
  recruiter: {
    title: 'Recruiter Portal',
    icon: Search,
    description: 'Verify candidate certificates',
    component: RecruiterDashboard,
  },
};

const UserTypeLayout: React.FC = () => {
  const { userType } = useParams<{ userType: string }>();

  if (!userType || !userTypeConfigs[userType]) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center p-8 bg-white rounded-lg shadow-md">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Invalid User Type</h2>
          <p className="text-gray-600 mb-6">The specified user type does not exist or is not supported.</p>
          <a
            href="/user-type"
            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
          >
            Back to User Selection
          </a>
        </div>
      </div>
    );
  }

  const { title, icon: Icon, description, component: UserTypeComponent } = userTypeConfigs[userType];

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8 flex items-center">
          <div className="flex-shrink-0 flex items-center">
            <div className="flex items-center justify-center h-12 w-12 rounded-md bg-blue-100 text-blue-600">
              <Icon className="h-6 w-6" />
            </div>
            <div className="ml-4">
              <h1 className="text-2xl font-bold text-gray-900">{title}</h1>
              <p className="text-sm text-gray-500">{description}</p>
            </div>
          </div>
          <nav className="ml-6 flex space-x-4">
            <a href="#" className="px-3 py-2 text-sm font-medium text-gray-900 rounded-md hover:bg-gray-100">
              Dashboard
            </a>
            <a href="#" className="px-3 py-2 text-sm font-medium text-gray-500 rounded-md hover:bg-gray-100">
              Profile
            </a>
            <a href="#" className="px-3 py-2 text-sm font-medium text-gray-500 rounded-md hover:bg-gray-100">
              Settings
            </a>
          </nav>
          <div className="ml-auto">
            <button className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <UserTypeComponent />
      </main>
    </div>
  );
};

export default UserTypeLayout;
