import React, { useState, useRef, useEffect } from 'react';
import { 
  Building, FileText, BarChart3, CheckCircle, QrCode, Camera, X, Loader2, Search, 
  Users, MapPin, Phone, Mail, ExternalLink, ChevronDown, Filter, Plus
} from 'lucide-react';
import { Html5Qrcode } from 'html5-qrcode';
import { Link } from 'react-router-dom';

interface Institution {
  id: string;
  name: string;
  type: string;
  location: string;
  email: string;
  phone: string;
  students: number;
  status: 'active' | 'pending' | 'suspended';
  registrationDate: string;
}

interface VerificationResult {
  isValid: boolean;
  message: string;
  certificateData?: {
    id: string;
    studentName: string;
    institution: string;
    issueDate: string;
    type: string;
  };
}

const AdminDashboard: React.FC = () => {
  // State management
  const [activeTab, setActiveTab] = useState('overview');
  const [isScanning, setIsScanning] = useState(false);
  const [scanResult, setScanResult] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [certificateId, setCertificateId] = useState('');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [verificationResult, setVerificationResult] = useState<VerificationResult | null>(null);
  const [isVerifying, setIsVerifying] = useState(false);
  const [verificationMethod, setVerificationMethod] = useState<'id' | 'file' | 'qr'>('id');
  const [institutions] = useState<Institution[]>([
    {
      id: 'INST001',
      name: 'Delhi Technological University',
      type: 'Government',
      location: 'New Delhi',
      email: 'admin@dtu.ac.in',
      phone: '+91 11 27871007',
      students: 12500,
      status: 'active',
      registrationDate: '2023-01-15'
    },
    {
      id: 'INST002',
      name: 'Indian Institute of Technology Delhi',
      type: 'Autonomous',
      location: 'New Delhi',
      email: 'office@iitd.ac.in',
      phone: '+91 11 26591753',
      students: 9800,
      status: 'active',
      registrationDate: '2023-02-20'
    },
    {
      id: 'INST003',
      name: 'Netaji Subhas University of Technology',
      type: 'State University',
      location: 'New Delhi',
      email: 'registrar@nsut.ac.in',
      phone: '+91 11 25099050',
      students: 8500,
      status: 'pending',
      registrationDate: '2023-03-10'
    }
  ]);

  const qrScannerRef = useRef<Html5Qrcode | null>(null);
  const qrContainerRef = useRef<HTMLDivElement>(null);

  // Stats for the overview tab
  const stats = [
    { label: 'Total Institutes', value: '1,245', change: '+12%', icon: Building },
    { label: 'Total Students', value: '245,678', change: '+8.5%', icon: Users },
    { label: 'Active Verifiers', value: '1,234', change: '+3%', icon: CheckCircle },
    { label: 'Fraud Cases', value: '45', change: '-2', icon: FileText }
  ];

  // Clean up QR Scanner on unmount
  useEffect(() => {
    return () => {
      if (qrScannerRef.current) {
        qrScannerRef.current.stop().catch(() => {});
        qrScannerRef.current = null;
      }
    };
  }, []);

  // Mock function to verify certificate by ID
  const verifyCertificateById = async (id: string): Promise<VerificationResult> => {
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Mock response - in a real app, this would be an API call
    if (id === 'CERT12345') {
      return {
        isValid: true,
        message: 'Certificate is valid',
        certificateData: {
          id: 'CERT12345',
          studentName: 'John Doe',
          institution: 'Delhi Technological University',
          issueDate: '2023-05-15',
          type: 'Degree Certificate'
        }
      };
    }
    return {
      isValid: false,
      message: 'Certificate not found or invalid'
    };
  };

  // Handle file upload
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setSelectedFile(e.target.files[0]);
    }
  };

  // Mock function to verify certificate by file
  const verifyCertificateByFile = async (file: File): Promise<VerificationResult> => {
    // Simulate file processing
    await new Promise(resolve => setTimeout(resolve, 1500));

    // In a real app, you would upload the file to your backend for verification
    return {
      isValid: true,
      message: 'Certificate verified successfully',
      certificateData: {
        id: 'CERT' + Math.floor(10000 + Math.random() * 90000),
        studentName: 'Jane Smith',
        institution: 'Indian Institute of Technology Delhi',
        issueDate: '2023-06-20',
        type: 'Transcript'
      }
    };
  };

  // Handle form submission
  const handleVerify = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsVerifying(true);
    setVerificationResult(null);

    try {
      let result: VerificationResult;

      if (verificationMethod === 'id' && certificateId) {
        result = await verifyCertificateById(certificateId);
      } else if (verificationMethod === 'file' && selectedFile) {
        result = await verifyCertificateByFile(selectedFile);
      } else if (verificationMethod === 'qr' && scanResult) {
        // For QR code, we'll use the scan result as the certificate ID
        result = await verifyCertificateById(scanResult);
      } else {
        throw new Error('Please provide the required information');
      }

      setVerificationResult(result);
    } catch (error) {
      setVerificationResult({
        isValid: false,
        message: error instanceof Error ? error.message : 'Verification failed'
      });
    } finally {
      setIsVerifying(false);
    }
  };

  // Start QR code scanner
  const startQRScanner = () => {
    setIsScanning(true);
    // In a real implementation, you would initialize the QR scanner here
    // For now, we'll simulate a QR scan after 2 seconds
    setTimeout(() => {
      setScanResult('CERT12345');
      setIsScanning(false);
      setVerificationMethod('qr');
    }, 2000);
  };

  // Stop QR code scanner
  const stopQRScanner = () => {
    setIsScanning(false);
    setScanResult(null);
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {stats.map((stat, index) => (
                <div key={index} className="bg-white rounded-lg shadow p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-500">{stat.label}</p>
                      <p className="text-2xl font-bold mt-1">{stat.value}</p>
                      <p className={`text-sm mt-1 ${stat.change.startsWith('+') ? 'text-green-600' : 'text-red-600'}`}>
                        {stat.change} from last month
                      </p>
                    </div>
                    <div className="p-3 bg-blue-50 rounded-full">
                      <stat.icon className="h-6 w-6 text-blue-600" />
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      case 'verifications':
        return (
          <div className="space-y-6">
            <div className="bg-white p-6 rounded-lg shadow">
              <h2 className="text-xl font-bold mb-6">Certificate Verification</h2>

              {/* Verification Method Tabs */}
              <div className="mb-6">
                <div className="flex space-x-4 border-b border-gray-200">
                  <button
                    type="button"
                    onClick={() => setVerificationMethod('id')}
                    className={`py-2 px-4 border-b-2 font-medium text-sm ${
                      verificationMethod === 'id'
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    Verify by ID
                  </button>
                  <button
                    type="button"
                    onClick={() => setVerificationMethod('file')}
                    className={`py-2 px-4 border-b-2 font-medium text-sm ${
                      verificationMethod === 'file'
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    Upload Certificate
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setVerificationMethod('qr');
                      startQRScanner();
                    }}
                    className={`py-2 px-4 border-b-2 font-medium text-sm ${
                      verificationMethod === 'qr'
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    Scan QR Code
                  </button>
                </div>
              </div>

              {/* Verification Form */}
              <form onSubmit={handleVerify} className="space-y-6">
                {verificationMethod === 'id' && (
                  <div>
                    <label htmlFor="certificateId" className="block text-sm font-medium text-gray-700 mb-1">
                      Certificate ID
                    </label>
                    <input
                      type="text"
                      id="certificateId"
                      value={certificateId}
                      onChange={(e) => setCertificateId(e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                      placeholder="Enter certificate ID"
                      required
                    />
                  </div>
                )}

                {verificationMethod === 'file' && (
                  <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                    <div className="space-y-1 text-center">
                      <svg
                        className="mx-auto h-12 w-12 text-gray-400"
                        stroke="currentColor"
                        fill="none"
                        viewBox="0 0 48 48"
                        aria-hidden="true"
                      >
                        <path
                          d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                          strokeWidth={2}
                          strokeLinecap="round"
                          strokeLinejoin="round"
                        />
                      </svg>
                      <div className="flex text-sm text-gray-600">
                        <label
                          htmlFor="file-upload"
                          className="relative cursor-pointer bg-white rounded-md font-medium text-blue-600 hover:text-blue-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blue-500"
                        >
                          <span>Upload a file</span>
                          <input
                            id="file-upload"
                            name="file-upload"
                            type="file"
                            className="sr-only"
                            onChange={handleFileChange}
                            accept=".pdf,.png,.jpg,.jpeg"
                            required={verificationMethod === 'file'}
                          />
                        </label>
                        <p className="pl-1">or drag and drop</p>
                      </div>
                      <p className="text-xs text-gray-500">
                        {selectedFile 
                          ? `Selected: ${selectedFile.name}` 
                          : 'PDF, JPG, PNG up to 10MB'}
                      </p>
                    </div>
                  </div>
                )}

                {verificationMethod === 'qr' && (
                  <div className="text-center">
                    <div className="mx-auto flex items-center justify-center h-64 w-64 bg-gray-100 rounded-md mb-4">
                      {isScanning ? (
                        <div className="text-center">
                          <Loader2 className="h-12 w-12 text-blue-500 animate-spin mx-auto mb-2" />
                          <p className="text-sm text-gray-500">Scanning for QR code...</p>
                        </div>
                      ) : scanResult ? (
                        <div className="text-green-600">
                          <CheckCircle className="h-12 w-12 mx-auto mb-2" />
                          <p className="text-sm">QR Code Scanned</p>
                          <p className="text-xs text-gray-500 mt-1">ID: {scanResult}</p>
                        </div>
                      ) : (
                        <div className="text-gray-400">
                          <QrCode className="h-12 w-12 mx-auto mb-2" />
                          <p className="text-sm">Point camera at QR code</p>
                        </div>
                      )}
                    </div>
                    {isScanning ? (
                      <button
                        type="button"
                        onClick={stopQRScanner}
                        className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                      >
                        <X className="h-4 w-4 mr-2" />
                        Cancel Scan
                      </button>
                    ) : (
                      <button
                        type="button"
                        onClick={startQRScanner}
                        className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                      >
                        <Camera className="h-4 w-4 mr-2" />
                        Start QR Scanner
                      </button>
                    )}
                  </div>
                )}

                <div className="pt-2">
                  <button
                    type="submit"
                    disabled={isVerifying}
                    className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isVerifying ? (
                      <>
                        <Loader2 className="animate-spin -ml-1 mr-2 h-4 w-4" />
                        Verifying...
                      </>
                    ) : (
                      'Verify Certificate'
                    )}
                  </button>
                </div>
              </form>

              {/* Verification Result */}
              {verificationResult && (
                <div className={`mt-6 p-4 rounded-md ${verificationResult.isValid ? 'bg-green-50' : 'bg-red-50'}`}>
                  <div className="flex">
                    <div className="flex-shrink-0">
                      {verificationResult.isValid ? (
                        <CheckCircle className="h-5 w-5 text-green-400" aria-hidden="true" />
                      ) : (
                        <X className="h-5 w-5 text-red-400" aria-hidden="true" />
                      )}
                    </div>
                    <div className="ml-3">
                      <h3 className={`text-sm font-medium ${verificationResult.isValid ? 'text-green-800' : 'text-red-800'}`}>
                        {verificationResult.isValid ? 'Verification Successful' : 'Verification Failed'}
                      </h3>
                      <div className={`mt-2 text-sm ${verificationResult.isValid ? 'text-green-700' : 'text-red-700'}`}>
                        <p>{verificationResult.message}</p>
                        {verificationResult.certificateData && (
                          <div className="mt-4 border-t border-gray-200 pt-4">
                            <h4 className="font-medium text-gray-900">Certificate Details:</h4>
                            <dl className="mt-2 grid grid-cols-1 gap-y-2">
                              <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">ID</dt>
                                <dd className="mt-1 text-sm text-gray-900">{verificationResult.certificateData.id}</dd>
                              </div>
                              <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">Student Name</dt>
                                <dd className="mt-1 text-sm text-gray-900">{verificationResult.certificateData.studentName}</dd>
                              </div>
                              <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">Institution</dt>
                                <dd className="mt-1 text-sm text-gray-900">{verificationResult.certificateData.institution}</dd>
                              </div>
                              <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">Issue Date</dt>
                                <dd className="mt-1 text-sm text-gray-900">
                                  {new Date(verificationResult.certificateData.issueDate).toLocaleDateString()}
                                </dd>
                              </div>
                              <div className="sm:col-span-1">
                                <dt className="text-sm font-medium text-gray-500">Type</dt>
                                <dd className="mt-1 text-sm text-gray-900">{verificationResult.certificateData.type}</dd>
                              </div>
                            </dl>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        );

      default:
        return (
          <div className="bg-white p-6 rounded-lg shadow">
            <h2 className="text-xl font-bold mb-4">
              {activeTab.charAt(0).toUpperCase() + activeTab.slice(1)}
            </h2>
            <p className="text-gray-600">This section is under development.</p>
          </div>
        );
    }
  };

  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg p-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-6">Admin Dashboard</h1>

        {/* Tabs */}
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {['overview', 'verifications', 'institutions', 'reports'].map((tab) => (
              <button
                key={tab}
                type="button"
                onClick={() => setActiveTab(tab)}
                className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
              </button>
            ))}
          </nav>
        </div>

        {/* Tab Content */}
        <div className="py-6">
          {renderTabContent()}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
