import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Building, GraduationCap, Users, Search, ArrowRight } from 'lucide-react';

const UserTypeSelection: React.FC = () => {
  const navigate = useNavigate();

  const userTypes = [
    {
      id: 'government',
      title: 'Government',
      description: 'Manage and verify official certificates',
      icon: Building,
      color: 'bg-blue-50 text-blue-600',
      iconColor: 'bg-blue-100',
      borderColor: 'border-blue-200',
      hoverColor: 'hover:border-blue-300',
      nextStep: 'login'
    },
    {
      id: 'institute',
      title: 'Institute',
      description: 'Issue and manage student certificates',
      icon: GraduationCap,
      color: 'bg-green-50 text-green-600',
      iconColor: 'bg-green-100',
      borderColor: 'border-green-200',
      hoverColor: 'hover:border-green-300',
      nextStep: 'register'
    },
    {
      id: 'student',
      title: 'Student',
      description: 'View and share your certificates',
      icon: Users,
      color: 'bg-purple-50 text-purple-600',
      iconColor: 'bg-purple-100',
      borderColor: 'border-purple-200',
      hoverColor: 'hover:border-purple-300',
      nextStep: 'login'
    },
    {
      id: 'recruiter',
      title: 'Recruiter/Verifier',
      description: 'Verify candidate certificates',
      icon: Search,
      color: 'bg-orange-50 text-orange-600',
      iconColor: 'bg-orange-100',
      borderColor: 'border-orange-200',
      hoverColor: 'hover:border-orange-300',
      nextStep: 'register'
    }
  ];

  const handleUserTypeSelect = (userType: string, nextStep: string) => {
    if (nextStep === 'register') {
      navigate(`/register/${userType}`);
    } else {
      navigate(`/login/${userType}`);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <section className="bg-gradient-to-br from-primary-50 to-primary-100 py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
              Choose Your <span className="text-primary-600">User Type</span>
            </h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto mb-8">
              Select your role to access the appropriate features and dashboard.
            </p>
          </div>

          <div className="mt-12 grid gap-8 md:grid-cols-2 lg:grid-cols-4">
            {userTypes.map((type) => (
              <div
                key={type.id}
                onClick={() => handleUserTypeSelect(type.id, type.nextStep)}
                className={`relative p-6 rounded-xl border-2 ${type.borderColor} ${type.hoverColor} bg-white cursor-pointer transition-all duration-200 hover:shadow-lg`}
              >
                <div className={`inline-flex p-3 rounded-lg ${type.iconColor} ${type.color} mb-4`}>
                  <type.icon className="h-6 w-6" />
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">{type.title}</h3>
                <p className="text-gray-600 mb-4">{type.description}</p>
                <div className="flex items-center text-sm font-medium text-blue-600">
                  {type.nextStep === 'register' ? 'Register Now' : 'Login'}
                  <ArrowRight className="ml-1 h-4 w-4" />
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default UserTypeSelection;
