import React, { useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { Building, GraduationCap, Users, Search, Eye, EyeOff, ArrowLeft, Shield, Award } from 'lucide-react';

const Login: React.FC = () => {
  const { userType } = useParams<{ userType: string }>();
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    loginType: userType || 'student',
    email: '',
    phone: '',
    password: '',
    rememberMe: false
  });

  const handleUserTypeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    navigate(`/login/${e.target.value}`);
  };

  const userTypeConfig = {
    government: {
      title: 'Government Department',
      icon: Building,
      color: 'bg-blue-50 text-blue-600',
      iconColor: 'bg-blue-100',
      description: 'Access admin certificate management dashboard'
    },
    institute: {
      title: 'Institute',
      icon: GraduationCap,
      color: 'bg-green-50 text-green-600',
      iconColor: 'bg-green-100',
      description: 'Manage student certificates and institutional records'
    },
    student: {
      title: 'Student',
      icon: Users,
      color: 'bg-purple-50 text-purple-600',
      iconColor: 'bg-purple-100',
      description: 'Access your personal certificate portfolio'
    },
    recruiter: {
      title: 'Recruiter/Verifier',
      icon: Search,
      color: 'bg-orange-50 text-orange-600',
      iconColor: 'bg-orange-100',
      description: 'Verify candidate certificates and credentials'
    },
    scholarship: {
      title: 'Scholarship Agency',
      icon: Award,
      color: 'bg-teal-50 text-teal-600',
      iconColor: 'bg-teal-100',
      description: 'Manage scholarship programs and verify applicants'
    }
  };

  const config = userTypeConfig[userType as keyof typeof userTypeConfig] || userTypeConfig.student;
  const Icon = config.icon;

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Handle login logic here
    console.log('Login attempt:', formData);
    navigate(`/dashboard/${userType}`);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        {/* Back Button */}
        <div className="mb-6">
          <Link
            to="/user-type"
            className="inline-flex items-center space-x-2 text-gray-600 hover:text-gray-900 transition-colors"
          >
            <ArrowLeft className="w-4 h-4" />
            <span>Back to User Types</span>
          </Link>
        </div>

        {/* Header */}
        <div className="text-center">
          <div className="mb-6">
            <label htmlFor="loginType" className="block text-sm font-medium text-gray-700 mb-2 text-left">
              Login As
            </label>
            <select
              id="loginType"
              name="loginType"
              value={formData.loginType}
              onChange={handleUserTypeChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500"
            >
              <option value="government">Government Department</option>
              <option value="institute">Institute</option>
              <option value="student">Student</option>
              <option value="recruiter">Recruiter/Verifier</option>
              <option value="scholarship">Scholarship Agency</option>
            </select>
          </div>
          <div className={`w-16 h-16 ${config.iconColor} rounded-full flex items-center justify-center mx-auto mb-4`}>
            <Icon className={`w-8 h-8 ${config.color.split(' ')[1]}`} />
          </div>
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            {config.title} Login
          </h2>
          <p className="text-gray-600 text-sm mb-4">
            {config.description}
          </p>
        </div>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
          <form className="space-y-6" onSubmit={handleSubmit}>
            {/* Email/Phone Field */}
            <div className="space-y-4">
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                  Email or Phone Number
                </label>
                <input
                  id="email"
                  name="email"
                  type="text"
                  autoComplete="email"
                  required
                  value={formData.email}
                  onChange={handleInputChange}
                  className="input-field"
                  placeholder="Enter your email or phone number"
                />
              </div>

              <div className="flex items-center">
                <div className="w-full border-t border-gray-300"></div>
                <span className="px-3 text-sm text-gray-500">or</span>
                <div className="w-full border-t border-gray-300"></div>
              </div>

              <div>
                <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-2">
                  Phone Number (with country code)
                </label>
                <input
                  id="phone"
                  name="phone"
                  type="tel"
                  autoComplete="tel"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className="input-field"
                  placeholder="+1 (555) 123-4567"
                />
              </div>
            </div>

            {/* Password Field */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-2">
                Password
              </label>
              <div className="relative">
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  autoComplete="current-password"
                  required
                  value={formData.password}
                  onChange={handleInputChange}
                  className="input-field pr-10"
                  placeholder="Enter your password"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <EyeOff className="h-5 w-5 text-gray-400" />
                  ) : (
                    <Eye className="h-5 w-5 text-gray-400" />
                  )}
                </button>
              </div>
            </div>

            {/* Remember Me & Forgot Password */}
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <input
                  id="rememberMe"
                  name="rememberMe"
                  type="checkbox"
                  checked={formData.rememberMe}
                  onChange={handleInputChange}
                  className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                />
                <label htmlFor="rememberMe" className="ml-2 block text-sm text-gray-700">
                  Remember me
                </label>
              </div>

              <div className="text-sm">
                <a href="#" className="font-medium text-primary-600 hover:text-primary-500">
                  Forgot password?
                </a>
              </div>
            </div>

            {/* Submit Button */}
            <div>
              <button
                type="submit"
                className="w-full btn-primary py-3 text-base font-medium"
              >
                Sign In
              </button>
            </div>

            {/* Register Link */}
            <div className="text-center">
              <p className="text-sm text-gray-600">
                Don't have an account?{' '}
                <Link
                  to={`/register/${userType}`}
                  className="font-medium text-primary-600 hover:text-primary-500"
                >
                  Register here
                </Link>
              </p>
            </div>
          </form>

          {/* Demo Credentials */}
          <div className="mt-6 p-4 bg-gray-50 rounded-lg">
            <h3 className="text-sm font-medium text-gray-700 mb-2">Demo Credentials:</h3>
            <div className="text-xs text-gray-600 space-y-1">
              <p><strong>Email:</strong> demo@{userType}.com</p>
              <p><strong>Password:</strong> demo123</p>
            </div>
          </div>
        </div>

        {/* Security Notice */}
        <div className="mt-6 text-center">
          <div className="flex items-center justify-center space-x-2 text-sm text-gray-500">
            <Shield className="w-4 h-4" />
            <span>Your data is protected with enterprise-grade security</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
