import React, { useState, useRef, useEffect } from 'react';
import { 
  Building, FileText, BarChart3, CheckCircle, QrCode, Camera, X, Loader2
} from 'lucide-react';
import { Html5Qrcode } from 'html5-qrcode';
import { Link } from 'react-router-dom';

const AdminDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [isScanning, setIsScanning] = useState(false);
  const [scanResult, setScanResult] = useState<string | null>(null);
  const qrScannerRef = useRef<Html5Qrcode | null>(null);
  const qrContainerRef = useRef<HTMLDivElement>(null);

  const stats = [
    { label: 'Total Institutes', value: '1,245', change: '+12%', icon: Building },
    { label: 'Total Students', value: '245,678', change: '+8.5%', icon: Building },
    { label: 'Active Verifiers', value: '1,234', change: '+3%', icon: CheckCircle },
    { label: 'Fraud Cases', value: '45', change: '-2', icon: FileText }
  ];

  // Clean up QR Scanner on unmount
  useEffect(() => {
    return () => {
      const cleanupScanner = async () => {
        if (qrScannerRef.current) {
          try {
            if (qrScannerRef.current.isScanning) {
              await qrScannerRef.current.stop();
            }
            qrScannerRef.current.clear();
            qrScannerRef.current = null;
          } catch (error) {
            console.error('Error cleaning up QR scanner:', error);
          }
        }
      };
      cleanupScanner();
    };
  }, []);

  const startQRScanner = async () => {
    // Clean up any existing scanner instance
    await stopQRScanner();

    try {
      setIsScanning(true);
      setScanResult(null);

      // Create new scanner instance
      const qrScanner = new Html5Qrcode('qr-reader');
      qrScannerRef.current = qrScanner;

      // Show the scanner modal first
      const modal = document.getElementById('qr-scanner-modal');
      if (modal) {
        modal.classList.remove('hidden');
      }

      // Start scanning
      await qrScanner.start(
        { facingMode: 'environment' },
        {
          fps: 10,
          qrbox: { width: 250, height: 250 },
        },
        (decodedText) => {
          // Handle successful scan
          setScanResult(decodedText);

          // Update verification result
          const resultDiv = document.createElement('div');
          resultDiv.className = 'mt-4 p-3 bg-green-100 text-green-800 rounded-md';
          resultDiv.innerHTML = `
            <p class="font-medium">✓ Certificate verified successfully!</p>
            <p class="text-sm mt-1">Certificate ID: ${decodedText}</p>
          `;
          const resultContainer = document.querySelector('.verification-result');
          if (resultContainer) {
            resultContainer.innerHTML = '';
            resultContainer.appendChild(resultDiv);
          }

          // Stop scanner after successful scan
          stopQRScanner();
        },
        (errorMessage) => {
          // Ignore errors when scanner is stopped intentionally
          if (errorMessage && !errorMessage.includes('NotFoundException')) {
            console.warn('QR Code scan error:', errorMessage);
          }
        }
      );
    } catch (err) {
      console.error('Error starting QR scanner:', err);
      await stopQRScanner();
    }
  };

  const stopQRScanner = async () => {
    try {
      if (qrScannerRef.current) {
        if (qrScannerRef.current.isScanning) {
          await qrScannerRef.current.stop();
        }
        qrScannerRef.current.clear();
        qrScannerRef.current = null;
      }
      setIsScanning(false);
      const modal = document.getElementById('qr-scanner-modal');
      if (modal) {
        modal.classList.add('hidden');
      }
    } catch (err) {
      console.error('Error stopping QR scanner:', err);
      setIsScanning(false);
    }
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {stats.map((stat, index) => (
                <div key={index} className="bg-white p-6 rounded-lg shadow">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-500">{stat.label}</p>
                      <p className="text-2xl font-semibold">{stat.value}</p>
                      <p className={`text-sm ${stat.change.startsWith('+') ? 'text-green-500' : 'text-red-500'}`}>
                        {stat.change}
                      </p>
                    </div>
                    <div className="p-3 bg-blue-100 rounded-full">
                      <stat.icon className="w-6 h-6 text-blue-600" />
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      case 'verify-certificate':
        return (
          <div className="space-y-6">
            <div className="bg-white p-6 rounded-lg shadow">
              <h2 className="text-xl font-bold mb-6">Verify Certificate</h2>
              <div className="space-y-6">
                {/* Method 1: QR Code Scan */}
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Quick Verify with QR Code</h3>
                  <div className="space-y-4">
                    <div className="flex justify-center">
                        <button
                          onClick={startQRScanner}
                          disabled={isScanning}
                          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-75 disabled:cursor-not-allowed"
                        >
                          {isScanning ? (
                            <>
                              <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                              Scanning...
                            </>
                          ) : (
                            <>
                              <QrCode className="h-4 w-4 mr-2" />
                              Scan QR Code
                            </>
                          )}
                        </button>
                    </div>
                  </div>
                </div>

                {/* Divider with "OR" */}
                <div className="relative">
                  <div className="absolute inset-0 flex items-center" aria-hidden="true">
                    <div className="w-full border-t border-gray-300"></div>
                  </div>
                  <div className="relative flex justify-center">
                    <span className="px-2 bg-white text-sm text-gray-500">OR</span>
                  </div>
                </div>

                {/* Method 2: Certificate ID Verification */}
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Verify by Certificate ID</h3>
                  <div className="space-y-4">
                    <div>
                      <label htmlFor="certificateId" className="block text-sm font-medium text-gray-700 mb-1">
                        Certificate ID
                      </label>
                      <div className="flex space-x-2">
                        <input
                          type="text"
                          id="certificateId"
                          name="certificateId"
                          className="flex-1 min-w-0 block w-full px-3 py-2 rounded-md border border-gray-300 shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                          placeholder="Enter Certificate ID"
                        />
                        <button
                          type="button"
                          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                        >
                          <CheckCircle className="h-4 w-4 mr-2" />
                          Verify ID
                        </button>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Divider with "OR" */}
                <div className="relative">
                  <div className="absolute inset-0 flex items-center" aria-hidden="true">
                    <div className="w-full border-t border-gray-300"></div>
                  </div>
                  <div className="relative flex justify-center">
                    <span className="px-2 bg-white text-sm text-gray-500">OR</span>
                  </div>
                </div>

                {/* Method 2: File Upload Verification */}
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Verify by Uploading Certificate</h3>
                  <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                    <div className="space-y-1 text-center">
                      <svg
                        className="mx-auto h-12 w-12 text-gray-400"
                        stroke="currentColor"
                        fill="none"
                        viewBox="0 0 48 48"
                        aria-hidden="true"
                      >
                        <path
                          d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                          strokeWidth={2}
                          strokeLinecap="round"
                          strokeLinejoin="round"
                        />
                      </svg>
                      <div className="flex text-sm text-gray-600">
                        <label
                          htmlFor="file-upload"
                          className="relative cursor-pointer bg-white rounded-md font-medium text-blue-600 hover:text-blue-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blue-500"
                        >
                          <span>Upload a file</span>
                          <input
                            id="file-upload"
                            name="file-upload"
                            type="file"
                            className="sr-only"
                            accept=".pdf,.png,.jpg,.jpeg"
                          />
                        </label>
                        <p className="pl-1">or drag and drop</p>
                      </div>
                      <p className="text-xs text-gray-500">PDF, JPG, PNG up to 10MB</p>
                    </div>
                  </div>
                  <div className="mt-2 flex justify-end">
                    <button
                      type="button"
                      className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      <CheckCircle className="h-4 w-4 mr-2" />
                      Verify File
                    </button>
                  </div>
                </div>

                {/* Verification Result Section */}
                <div className="border-t border-gray-200 pt-4 mt-6">
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Verification Result</h3>
                    <div className="verification-result bg-gray-50 p-4 rounded-lg border border-gray-200">
                      <p className="text-center text-gray-500">
                        Scan a QR code, enter a Certificate ID, or upload a certificate file to verify its validity
                      </p>
                    </div>
                </div>

                {/* QR Scanner Modal */}
                <div id="qr-scanner-modal" className="fixed z-10 inset-0 overflow-y-auto hidden">
                  <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
                    <div className="fixed inset-0 transition-opacity" aria-hidden="true">
                      <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
                    </div>
                    <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
                    <div className="inline-block align-bottom bg-white rounded-lg px-4 pt-5 pb-4 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full sm:p-6">
                      <div className="absolute top-0 right-0 pt-4 pr-4">
                        <button
                          type="button"
                          className="bg-white rounded-md text-gray-400 hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                          onClick={() => document.getElementById('qr-scanner-modal')?.classList.add('hidden')}
                        >
                          <span className="sr-only">Close</span>
                          <X className="h-6 w-6" aria-hidden="true" />
                        </button>
                      </div>
                      <div>
                        <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-indigo-100">
                          <QrCode className="h-6 w-6 text-indigo-600" aria-hidden="true" />
                        </div>
                        <div className="mt-3 text-center sm:mt-5">
                          <h3 className="text-lg leading-6 font-medium text-gray-900">Scan QR Code</h3>
                          <div className="mt-4">
                            <div id="qr-reader" className="w-full h-64 bg-black rounded-lg overflow-hidden relative">
                      <div className="absolute inset-0 flex items-center justify-center">
                        <div className="relative w-64 h-64 border-4 border-transparent rounded-lg">
                          <div className="absolute top-0 left-0 w-full h-1 bg-red-500 animate-pulse"></div>
                          <div className="absolute top-0 right-0 w-1 h-full bg-red-500 animate-pulse"></div>
                          <div className="absolute bottom-0 left-0 w-full h-1 bg-red-500 animate-pulse"></div>
                          <div className="absolute top-0 left-0 w-1 h-full bg-red-500 animate-pulse"></div>
                        </div>
                      </div>
                      {!isScanning && (
                        <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50">
                          <p className="text-white text-sm bg-black bg-opacity-70 px-3 py-2 rounded text-center">
                            {navigator.mediaDevices ? 'Click "Start Camera" to begin scanning' : 'Camera access not available'}
                          </p>
                        </div>
                      )}
                    </div>
                            </div>
                            <p className="mt-2 text-sm text-gray-500">
                              Point your camera at a certificate's QR code to verify
                            </p>
                          </div>
                        </div>
                      </div>
                      <div className="mt-5 sm:mt-6">
                        <div className="flex space-x-3">
                      <button
                        type="button"
                        onClick={stopQRScanner}
                        className="flex-1 inline-flex justify-center items-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:text-sm"
                      >
                        <X className="h-4 w-4 mr-2" />
                        Cancel
                      </button>
                      {!isScanning && (
                        <button
                          type="button"
                          onClick={startQRScanner}
                          className="flex-1 inline-flex justify-center items-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-indigo-600 text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:text-sm"
                        >
                          <Camera className="h-4 w-4 mr-2" />
                          Start Camera
                        </button>
                      )}
                      {isScanning && (
                        <button
                          type="button"
                          disabled
                          className="flex-1 inline-flex justify-center items-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-indigo-400 text-base font-medium text-white cursor-not-allowed sm:text-sm"
                        >
                          <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                          Scanning...
                        </button>
                      )}
                    </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
// Additional tab content here
          </div>
        );
      default:
        return (
          <div className="bg-white p-6 rounded-lg shadow">
            <h2 className="text-xl font-bold mb-4">{activeTab.charAt(0).toUpperCase() + activeTab.slice(1)}</h2>
            <p className="text-gray-600">This section is under development.</p>
          </div>
        );
    }
  };

  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg p-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-6">Admin Dashboard</h1>

        {/* Tabs */}
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {['overview', 'verify-certificate', 'reports', 'settings'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}
              </button>
            ))}
          </nav>
        </div>

        {/* Tab Content */}
        <div className="py-6">
          {renderTabContent()}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
