import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import './App.css';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Home from './pages/Home';
import About from './pages/About';
import Benefits from './pages/Benefits';
import UserTypeSelectionLogin from './pages/UserTypeSelectionLogin';
import UserTypeSelectionRegister from './pages/UserTypeSelectionRegister';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Layout from './components/Layout';
import ProtectedRoute from './components/ProtectedRoute';

// A wrapper to handle redirecting authenticated users from public routes
function PublicRoute({ children }: { children: JSX.Element }) {
  const { user, isAuthenticated } = useAuth();
  const location = useLocation();

  if (isAuthenticated && user) {
    // If user is logged in, redirect to their dashboard
    return <Navigate to={`/dashboard/${user.role}`} replace />;
  }

  return children;
}

function App() {
  return (
    <Router>
      <AuthProvider>
        <Layout>
          <Routes>
            <Route path="/" element={
              <PublicRoute>
                <Home />
              </PublicRoute>
            } />
            <Route path="/about" element={<About />} />
            <Route path="/benefits" element={<Benefits />} />
            <Route path="/user-type" element={
              <PublicRoute>
                <UserTypeSelectionLogin />
              </PublicRoute>
            } />
            <Route path="/register-type" element={
              <PublicRoute>
                <UserTypeSelectionRegister />
              </PublicRoute>
            } />
            <Route path="/login/:userType" element={
              <PublicRoute>
                <Login />
              </PublicRoute>
            } />
            <Route path="/register/:userType" element={
              <PublicRoute>
                <Register />
              </PublicRoute>
            } />
            <Route path="/dashboard/:userType" element={
              <ProtectedRoute>
                <Dashboard />
              </ProtectedRoute>
            } />
          </Routes>
        </Layout>
      </AuthProvider>
    </Router>
  );
}

export default App;
