import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  Building, GraduationCap, Users, Search, 
  Shield, CheckCircle, AlertTriangle, TrendingUp, 
  FileText, Download, Upload, Settings, 
  Bell, LogOut, Plus, Filter, Search as SearchIcon,
  BarChart3, Calendar, Clock, Award, Eye
} from 'lucide-react';

// Define TypeScript interfaces
interface Institute {
  id: string;
  name: string;
  address: string;
  contact: string;
  date: string;
}

interface Report {
  id: string;
  name: string;
  type: string;
  date: string;
  status: string;
}

interface StatItem {
  label: string;
  value: string;
  change: string;
  icon: React.ComponentType<{ className?: string }>;
}

interface UserTypeConfig {
  title: string;
  icon: React.ComponentType<{ className?: string }>;
  color: string;
  iconColor: string;
  tabs: string[];
  stats: StatItem[];
}

const Dashboard: React.FC = () => {
  const { userType = 'government' } = useParams<{ userType: string }>();
  const [activeTab, setActiveTab] = useState('overview');

  // State for data
  const [pendingInstitutes, setPendingInstitutes] = useState<Institute[]>([
    { id: 'INST-001', name: 'ABC University', address: '123 Education St, City', contact: 'contact@abc.edu', date: '2024-01-10' },
    { id: 'INST-002', name: 'XYZ College', address: '456 Knowledge Ave, Town', contact: 'admin@xyz.edu', date: '2024-01-12' },
  ]);

  const [reports, setReports] = useState<Report[]>([
    { id: 'REP-001', name: 'Monthly Verification Report', type: 'PDF', date: '2024-01-01', status: 'Generated' },
  ]);

  // Handler functions
  const handleApprove = (instituteId: string) => {
    setPendingInstitutes(pendingInstitutes.filter(inst => inst.id !== instituteId));
  };

  const handleReject = (instituteId: string) => {
    setPendingInstitutes(pendingInstitutes.filter(inst => inst.id !== instituteId));
  };

  const handleImposeFine = (certificateId: string) => {
    alert(`Imposing fine for certificate ${certificateId}`);
  };

  const generateReport = () => {
    const newReport: Report = {
      id: `REP-${Math.floor(1000 + Math.random() * 9000)}`,
      name: `Report_${new Date().toISOString().split('T')[0]}`,
      type: 'PDF',
      date: new Date().toISOString().split('T')[0],
      status: 'Generated'
    };
    setReports([newReport, ...reports]);
  };

  const userTypeConfig: Record<string, UserTypeConfig> = {
    government: {
      title: 'Admin Dashboard',
      icon: Building,
      color: 'bg-blue-50 text-blue-600',
      iconColor: 'bg-blue-100',
      tabs: ['overview', 'requests', 'institutions', 'reports'],
      stats: [
        { label: 'Pending Approvals', value: '23', change: '', icon: Clock },
        { label: 'Active Institutions', value: '456', change: '', icon: Building },
        { label: 'Fraud Detected', value: '23', change: '', icon: AlertTriangle },
        { label: 'Total Requests', value: '89', change: '', icon: FileText }
      ]
    },
    institute: {
      title: 'Institute Dashboard',
      icon: GraduationCap,
      color: 'bg-green-50 text-green-600',
      iconColor: 'bg-green-100',
      tabs: ['overview', 'students', 'certificates', 'analytics'],
      stats: [
        { label: 'Total Students', value: '2,456', change: '+15%', icon: Users },
        { label: 'Certificates Issued', value: '1,890', change: '+22%', icon: Award },
        { label: 'Pending Verification', value: '45', change: '-8%', icon: Clock },
        { label: 'Success Rate', value: '98.5%', change: '+2%', icon: TrendingUp }
      ]
    },
    student: {
      title: 'Student Dashboard',
      icon: Users,
      color: 'bg-purple-50 text-purple-600',
      iconColor: 'bg-purple-100',
      tabs: ['overview', 'certificates', 'portfolio', 'sharing'],
      stats: [
        { label: 'My Certificates', value: '12', change: '+3', icon: FileText },
        { label: 'Verified', value: '11', change: '+2', icon: CheckCircle },
        { label: 'Shared', value: '8', change: '+1', icon: Users },
        { label: 'Views', value: '156', change: '+24', icon: Eye }
      ]
    },
    recruiter: {
      title: 'Recruiter Dashboard',
      icon: Search,
      color: 'bg-orange-50 text-orange-600',
      iconColor: 'bg-orange-100',
      tabs: ['overview', 'verifications', 'candidates', 'reports'],
      stats: [
        { label: 'Verifications Today', value: '89', change: '+18%', icon: Search },
        { label: 'Successful', value: '84', change: '+12%', icon: CheckCircle },
        { label: 'Fraud Detected', value: '5', change: '-2', icon: AlertTriangle },
        { label: 'Avg. Response Time', value: '2.3s', change: '-0.5s', icon: Clock }
      ]
    }
  };

  const config = userTypeConfig[userType as keyof typeof userTypeConfig] || userTypeConfig.student;
  const Icon = config.icon;

  const renderGovernmentOverview = () => (
    <div className="space-y-6">
      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {[
          { label: 'Total Institutes', value: '1,245', change: '+12%', icon: Building },
          { label: 'Total Students', value: '245,678', change: '+8.5%', icon: Users },
          { label: 'Active Verifiers', value: '1,234', change: '+3%', icon: Shield },
          { label: 'Fraud Cases', value: '45', change: '-2', icon: AlertTriangle }
        ].map((stat, index) => {
          const StatIcon = stat.icon;
          return (
            <div key={stat.label} className="card group hover:shadow-lg transition-all duration-300">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 mb-1">{stat.label}</p>
                  <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
                  {stat.change && (
                    <p className={`text-sm ${stat.change.startsWith('+') ? 'text-green-600' : stat.change.startsWith('-') ? 'text-red-600' : 'text-gray-600'}`}>
                      {stat.change}
                    </p>
                  )}
                </div>
                <div className={`w-12 h-12 ${config.iconColor} rounded-lg flex items-center justify-center group-hover:scale-110 transition-transform`}>
                  <StatIcon className={`w-6 h-6 ${config.color.split(' ')[1]}`} />
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="card">
          <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>
          <div className="space-y-3">
            <button className="w-full btn-primary">
              <Plus className="w-4 h-4 mr-2" /> Add New Institute
            </button>
            <button className="w-full btn-secondary">
              <FileText className="w-4 h-4 mr-2" /> Generate Report
            </button>
            <button className="w-full bg-red-100 text-red-700 hover:bg-red-200 px-4 py-2 rounded-lg text-sm font-medium transition-colors">
              <AlertTriangle className="w-4 h-4 mr-2 inline" />
              View Alerts (3)
            </button>
          </div>
        </div>

        <div className="md:col-span-2 card">
          <h3 className="text-lg font-semibold mb-4">Recent Activities</h3>
          <div className="space-y-4">
            {[
              { id: 1, action: 'New institute registration: LMN College', time: '2 hours ago', type: 'registration' },
              { id: 2, action: 'Fraud detected: Certificate #CERT-102', time: '5 hours ago', type: 'alert' },
              { id: 3, action: 'Monthly report generated', time: '1 day ago', type: 'report' },
            ].map(activity => (
              <div key={activity.id} className="flex items-start space-x-3">
                <div className={`w-2 h-2 mt-2 rounded-full ${
                  activity.type === 'registration' ? 'bg-blue-500' : 
                  activity.type === 'alert' ? 'bg-red-500' : 'bg-green-500'
                }`}></div>
                <div>
                  <p className="text-sm text-gray-900">{activity.action}</p>
                  <p className="text-xs text-gray-500">{activity.time}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  const renderCertificates = () => (
    <div className="space-y-6">
      {/* Search and Filters */}
      <div className="card">
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1 relative">
            <SearchIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search certificates..."
              className="input-field pl-10"
            />
          </div>
          <button className="btn-secondary flex items-center space-x-2">
            <Filter className="w-4 h-4" />
            <span>Filter</span>
          </button>
        </div>
      </div>

      {/* Certificates Table */}
      <div className="card">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Certificate ID
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Student Name
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Institution
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Date
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {[
                { id: 'CERT-001', name: 'John Doe', institution: 'ABC University', status: 'Verified', date: '2024-01-15' },
                { id: 'CERT-002', name: 'Jane Smith', institution: 'XYZ College', status: 'Pending', date: '2024-01-14' },
                { id: 'CERT-003', name: 'Mike Johnson', institution: 'DEF Institute', status: 'Verified', date: '2024-01-13' },
                { id: 'CERT-004', name: 'Sarah Wilson', institution: 'GHI University', status: 'Fraud', date: '2024-01-12' }
              ].map((cert, index) => (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {cert.id}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {cert.name}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {cert.institution}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      cert.status === 'Verified' ? 'bg-green-100 text-green-800' :
                      cert.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-red-100 text-red-800'
                    }`}>
                      {cert.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {cert.date}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2">
                      <button className="text-primary-600 hover:text-primary-900">
                        <Eye className="w-4 h-4" />
                      </button>
                      <button className="text-gray-600 hover:text-gray-900">
                        <Download className="w-4 h-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderRequestsTab = () => (
    <div className="space-y-6">
      <div className="card">
        <h2 className="text-xl font-bold mb-6">Pending Institute Requests</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Institute ID</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Address</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Contact</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Registration Date</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {pendingInstitutes.map((institute) => (
                <tr key={institute.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{institute.id}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{institute.name}</td>
                  <td className="px-6 py-4 text-sm text-gray-900">{institute.address}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{institute.contact}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{institute.date}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2">
                      <button 
                        onClick={() => handleApprove(institute.id)}
                        className="text-green-600 hover:text-green-900"
                      >
                        Approve
                      </button>
                      <button 
                        onClick={() => handleReject(institute.id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        Reject
                      </button>
                      <button className="text-blue-600 hover:text-blue-900">
                        View Details
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  // Sample data for institutions
  const registeredInstitutions = [
    { id: 'I001', name: 'ABC University', location: 'Mumbai', contact: 'contact@abc.edu', registrationDate: '2023-01-15' },
    { id: 'I002', name: 'XYZ College', location: 'Delhi', contact: 'info@xyz.edu', registrationDate: '2023-02-20' },
    { id: 'I003', name: 'PQR Institute', location: 'Bangalore', contact: 'admin@pqr.edu', registrationDate: '2023-03-10' },
  ];

  const nonRegisteredInstitutions = [
    { id: 'N001', name: 'LMN College', location: 'Chennai', contact: 'contact@lmn.edu' },
    { id: 'N002', name: 'RST University', location: 'Hyderabad', contact: 'info@rst.edu' },
  ];

  const renderInstitutionsTab = () => (
    <div className="space-y-8">
      {/* Registered Institutions */}
      <div className="card">
        <h2 className="text-xl font-bold mb-6">Registered Institutions</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Institution Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Location</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Contact</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Registration Date</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {registeredInstitutions.map((inst) => (
                <tr key={inst.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{inst.id}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{inst.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{inst.location}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{inst.contact}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{inst.registrationDate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Non-Registered Institutions */}
      <div className="card">
        <h2 className="text-xl font-bold mb-6">Non-Registered Institutions</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Institution Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Location</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Contact</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {nonRegisteredInstitutions.map((inst) => (
                <tr key={inst.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{inst.id}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{inst.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{inst.location}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{inst.contact}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <button className="text-indigo-600 hover:text-indigo-900 mr-4">Register</button>
                    <button className="text-red-600 hover:text-red-900">Reject</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderReportsTab = () => (
    <div className="space-y-6">
      <div className="card">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-bold">Reports</h2>
          <button 
            onClick={generateReport}
            className="btn-primary flex items-center"
          >
            <Plus className="w-4 h-4 mr-2" /> Generate New Report
          </button>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Report ID</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date Generated</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {reports.map((report) => (
                <tr key={report.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{report.id}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{report.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{report.type}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{report.date}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className="px-2 py-1 text-xs rounded-full bg-green-100 text-green-800">
                      {report.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-3">
                      <button className="text-blue-600 hover:text-blue-900 flex items-center">
                        <Download className="w-4 h-4 mr-1" /> Download
                      </button>
                      <button className="text-gray-600 hover:text-gray-900">
                        Share
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderTabContent = () => {
    if (userType === 'government') {
      switch (activeTab) {
        case 'overview':
          return renderGovernmentOverview();
        case 'requests':
          return renderRequestsTab();
        case 'institutions':
          return renderInstitutionsTab();
        case 'reports':
          return renderReportsTab();
        default:
          return renderGovernmentOverview();
      }
    } else {
      switch (activeTab) {
        case 'overview':
          // For non-government users, show a different overview
          return (
            <div className="card">
              <h2 className="text-xl font-bold mb-4">Welcome to {config.title}</h2>
              <p className="text-gray-600">Select an option from the menu to get started.</p>
            </div>
          );
        case 'certificates':
          return renderCertificates();
        default:
          return (
            <div className="card text-center py-12">
              <div className={`w-16 h-16 ${config.iconColor} rounded-full flex items-center justify-center mx-auto mb-4`}>
                <Icon className={`w-8 h-8 ${config.color.split(' ')[1]}`} />
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                {activeTab.charAt(0).toUpperCase() + activeTab.slice(1)} Section
              </h3>
              <p className="text-gray-600">
                This section is under development. More features coming soon!
              </p>
            </div>
          );
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header with Tabs */}
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center space-x-4 mb-4">
            <div className={`w-10 h-10 ${config.iconColor} rounded-lg flex items-center justify-center`}>
              <Icon className={`w-6 h-6 ${config.color.split(' ')[1]}`} />
            </div>
            <h1 className="text-2xl font-bold text-gray-900">{config.title}</h1>
          </div>

          {/* Tabs */}
          <nav className="flex space-x-8">
            {config.tabs.map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`py-2 px-1 border-b-2 font-medium text-sm transition-colors ${
                  activeTab === tab
                    ? 'border-primary-500 text-primary-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
              </button>
            ))}
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {renderTabContent()}
      </div>
    </div>
  );
};

export default Dashboard;
